import React, { useMemo, useCallback } from "react";
import { useTable, useRowSelect } from "react-table";
import { useAuthContext } from '../hooks/useAuthContext';

const UsersDetails = ({ users }) => {
  const COLUMNS = [
    {
      Header: "Check",
      accessor: "check",
      Cell: ({ row }) => <input type="checkbox" {...row.getToggleRowSelectedProps()} />,
    },
    {
      Header: "Name",
      accessor: "fullname",
    },
    {
      Header: "Position",
      accessor: "position",
    },
    {
      Header: "e-Mail",
      accessor: "email",
    },
    {
      Header: "Last login",
      accessor: "lastlogin",
    },
    {
      Header: "Status",
      accessor: "blocked",
      Cell: ({ cell }) => (cell.value ? "Blocked" : "Active"),
    }
  ];

  const columns = useMemo(() => COLUMNS, []);
  const data = useMemo(() => users, []);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    selectedFlatRows,
  } = useTable(
    {
      columns,
      data,
    },
    useRowSelect
  );

  // Access array of all selected users
  const selectedUsers = selectedFlatRows.map((row) => row.original);
  console.log(selectedUsers);

  //MOVE TO SEPERATE FILE
  const blockUsers = () => {selectedUsers.map((usr)=>{
    const blockU = async () =>{
      await fetch('api/user/' + usr._id, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          blocked: true
        }),
      });
    }

    blockU();
  })};
  
  const unblockUsers = () => {selectedUsers.map((usr)=>{
    const unblockU = async () =>{
      await fetch('api/user/' + usr._id, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          blocked: false
        }),
      });
    }

    unblockU();
  })};
  
  const deleteUsers = () => {selectedUsers.map((usr)=>{
    const deleteU = async ()=>{
      await fetch('/api/user/' + usr._id, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${user.token}`
        }
      })
    }

    deleteU();
  })};
  

//////////////////////////////////////////

  return (
    <div>
      {/* Add your buttons here */}
      <button onClick={() => blockUsers()}>Block</button>
      <button onClick={() => unblockUsers()}>Unblock</button>
      <button onClick={() => deleteUsers()}>Delete</button>

      <table {...getTableProps()} style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column, columnIndex) => (
                <th
                  {...column.getHeaderProps()}
                  style={{
                    borderBottom: "2px solid #ddd",
                    borderRight: columnIndex < headerGroup.headers.length - 1 ? "1px solid #ddd" : "none",
                    padding: "8px",
                  }}
                >
                  {column.render("Header")}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell, cellIndex) => (
                  <td
                    {...cell.getCellProps()}
                    style={{
                      borderBottom: "1px solid #ddd",
                      borderRight: cellIndex < row.cells.length - 1 ? "1px solid #ddd" : "none",
                      padding: "8px",
                    }}
                  >
                    {cell.render("Cell")}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default UsersDetails;
