import React, { useMemo, useCallback } from "react";
import { useTable, useRowSelect } from "react-table";

const UsersDetails = ({ users }) => {
  const COLUMNS = [
    {
      Header: "Check",
      accessor: "check",
      Cell: ({ row }) => <input type="checkbox" {...row.getToggleRowSelectedProps()} />,
    },
    {
      Header: "Name",
      accessor: "fullname",
    },
    {
      Header: "Position",
      accessor: "position",
    },
    {
      Header: "e-Mail",
      accessor: "email",
    },
    {
      Header: "Last login",
      accessor: "lastlogin",
    },
    {
      Header: "Status",
      accessor: "status",
      Cell: ({ cell }) => (cell.value ? "Blocked" : "Active"),
    },
  ];

  const columns = useMemo(() => COLUMNS, []);
  const data = useMemo(() => users, []);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    selectedFlatRows,
  } = useTable(
    {
      columns,
      data,
    },
    useRowSelect
  );

  const selectedUsers = selectedFlatRows.map((row) => row.original);

  const blockUsers = useCallback(async () => {
    try {
      // Assuming you have an API endpoint for updating the status of selected users
      const response = await fetch('https://your-api-endpoint.com/block-users', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          users: selectedUsers.map(user => ({ id: user.id, status: false })),
        }),
      });

      if (response.ok) {
        console.log('Users blocked successfully!');
        // You may want to refresh the user data or update the local state accordingly
      } else {
        console.error('Failed to block users');
      }
    } catch (error) {
      console.error('Error blocking users:', error);
    }
  }, [selectedUsers]);

  return (
    <div>
      <button onClick={blockUsers}>Block</button>
      {/* Add your other buttons here */}
      <button onClick={() => console.log("Button 2 clicked")}>Unblock</button>
      <button onClick={() => console.log("Button 3 clicked")}>Delete</button>

      {/* ... Rest of your component code ... */}
    </div>
  );
};

export default UsersDetails;
