import React, { useMemo } from "react";
import { useTable, useRowSelect } from "react-table";

const UsersDetails = ({ users }) => {
  const COLUMNS = [
    {
      Header: "Check",
      accessor: "check",
      Cell: ({ row }) => <input type="checkbox" {...row.getToggleRowSelectedProps()} />,
    },
    {
      Header: "Name",
      accessor: "fullname",
    },
    {
      Header: "Position",
      accessor: "position",
    },
    {
      Header: "e-Mail",
      accessor: "email",
    },
    {
      Header: "Last login",
      accessor: "lastlogin",
    },
    {
      Header: "Status",
      accessor: "status",
      Cell: ({ cell }) => (cell.value === false ? "Active" : "Blocked"),
    },
  ];

  const columns = useMemo(() => COLUMNS, []);
  const data = useMemo(() => users, []);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    selectedFlatRows,
  } = useTable(
    {
      columns,
      data,
    },
    useRowSelect
  );

  // Access array of all selected users
  const selectedUsers = selectedFlatRows.map((row) => row.original);

  return (
    <>
      <table {...getTableProps()} style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()} style={{ borderBottom: "2px solid #ddd", padding: "8px" }}>
                  {column.render("Header")}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()} style={{ borderBottom: "1px solid #ddd", padding: "8px" }}>
                    {cell.render("Cell")}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>

      <div style={{ marginTop: "20px" }}>
        <h3>Selected Users:</h3>
        <ul>
          {selectedUsers.map((user) => (
            <li key={user.id}>{user.fullname}</li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default UsersDetails;
