// Table
import { useMemo } from 'react';
import { useTable } from 'react-table';

const UsersDetails = ({users}) => {
    const [selectedUsers, setSelectedUsers] = useState([]);

    const handleCheckboxChange = (user) => {
        const updatedUsers = [...selectedUsers];
    
        if (user.check) {
          // User is checked, so remove from array
          const index = updatedUsers.findIndex((u) => u.id === user.id);
          updatedUsers.splice(index, 1);
        } else {
          // User is unchecked, so add to array
          updatedUsers.push(user);
        }
    
        setSelectedUsers(updatedUsers);
      };

    const COLUMNS = [
        {
            Header: "Check",
            accessor: "check", // Add a virtual accessor for the check column
            Cell: ({ row }) => (
              <input type="checkbox" checked={row.original.check} readOnly 
              onChange={() => handleCheckboxChange(row.original)}
              />
            ),
        },
        {
           Header: "Name",
           accessor: "fullname"
        },
        {
           Header: "Position",
           accessor: "position"
        },
        {
           Header: "e-Mail",
           accessor: "email"
        },
        {
           Header: "Last login",
           accessor: "lastlogin"
        },
        {
           Header: "Status",
           accessor: "status"
        }
    ];

    const columns = useMemo(()=> COLUMNS, []);
    const data = useMemo(()=> users, []);
    console.log(data);

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data });

    return (
        <div className="App">
          <div className="container">
            <table {...getTableProps()}>
              <thead>
                {headerGroups.map((headerGroup) => (
                  <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column) => (
                      <th {...column.getHeaderProps()}>
                        {column.render("Header")}
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody {...getTableBodyProps()}>
                {rows.map((row) => {
                  prepareRow(row);
                  return (
                    <tr {...row.getRowProps()}>
                      {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}> {cell.render("Cell")} </td>
                      ))}
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      );
}
 
export default UsersDetails;
