import React, { useMemo, useCallback } from "react";
import { useTable, useRowSelect } from "react-table";
import { useAuthContext } from '../hooks/useAuthContext';

const UsersDetails = ({ users }) => {
  const { user } = useAuthContext();

  const COLUMNS = [
    {
      Header: "Check",
      accessor: "check",
      Cell: ({ row }) => <input type="checkbox" {...row.getToggleRowSelectedProps()} />,
    },
    {
      Header: "Name",
      accessor: "fullname",
    },
    {
      Header: "Position",
      accessor: "position",
    },
    {
      Header: "e-Mail",
      accessor: "email",
    },
    {
      Header: "Last login",
      accessor: "lastlogin",
    },
    {
      Header: "Status",
      accessor: "blocked",
      Cell: ({ cell }) => (cell.value ? "Blocked" : "Active"),
    }
  ];

  const columns = useMemo(() => COLUMNS, []);
  const data = useMemo(() => users, []);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    selectedFlatRows,
  } = useTable(
    {
      columns,
      data,
    },
    useRowSelect
  );

  // Access array of all selected users
  const selectedUsers = selectedFlatRows.map((row) => row.original);
  console.log(selectedUsers);

  //MOVE TO SEPERATE FILE
  const blockUsers = useCallback(async () => {
    try {
      // Assuming you have an API endpoint for updating the status of selected users
      const response = await fetch('api/user', {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${user.token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          ids: selectedUsers.map(user => user.id),
          blocked: true,
        }),
      });
  
      if (response.ok) {
        console.log('Users blocked successfully!');
        // You may want to refresh the user data or update the local state accordingly
      } else {
        console.error('Failed to block users');
      }
    } catch (error) {
      console.error('Error blocking users:', error);
    }
  }, [selectedUsers]);

//////////////////////////////////////////

  return (
    <div>
      {/* Add your buttons here */}
      <button onClick={() => console.log("Button 1 clicked")}>Block</button>
      <button onClick={() => console.log("Button 2 clicked")}>Unblock</button>
      <button onClick={() => console.log("Button 3 clicked")}>Delete</button>

      <table {...getTableProps()} style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column, columnIndex) => (
                <th
                  {...column.getHeaderProps()}
                  style={{
                    borderBottom: "2px solid #ddd",
                    borderRight: columnIndex < headerGroup.headers.length - 1 ? "1px solid #ddd" : "none",
                    padding: "8px",
                  }}
                >
                  {column.render("Header")}
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell, cellIndex) => (
                  <td
                    {...cell.getCellProps()}
                    style={{
                      borderBottom: "1px solid #ddd",
                      borderRight: cellIndex < row.cells.length - 1 ? "1px solid #ddd" : "none",
                      padding: "8px",
                    }}
                  >
                    {cell.render("Cell")}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default UsersDetails;
