import React, { useEffect, useState } from "react";
import { useAuthContext } from '../hooks/useAuthContext';
import UsersDetails from '../components/UsersDetails';

const Main = () => {
    const { user, dispatch, logout } = useAuthContext();
    const [users, setUsers] = useState(null);
    const [userDeletedFlag, setUserDeletedFlag] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('/api/user', {
                    method: 'GET',
                    headers: { 'Authorization': `Bearer ${user.token}` }
                });
                const data = await response.json();
                if (data.length === 0) {
                    dispatch({ type: 'LOGOUT' });
                    localStorage.removeItem('user');
                    setUserDeletedFlag(true); // Set the flag to true when a user is deleted
                } else {
                    setUserDeletedFlag(false); // Set the flag to false when users are present
                }
                console.log("data: " + data.length);
                setUsers(data);
            } catch (error) {
                console.error('Error fetching user data', error);
            }
        };

        fetchData();
    }, [user, userDeletedFlag]);

    const COLUMNS = [
        {
            Header: "Name",
            accessor: "fullname"
        },
        {
            Header: "Position",
            accessor: "position"
        },
        {
            Header: "e-Mail",
            accessor: "email"
        },
        {
            Header: "Last login",
            accessor: "lastlogin"
        },
        {
            Header: "Status",
            accessor: "status"
        }
    ];

    return (
        <div className="main">
            {users && <UsersDetails users={users} />}
        </div>
    );
}

export default Main;
