import React, { useState } from "react";
import { usePagination, useRowSelect } from "react-table";

// Table
import { useMemo } from 'react';
import { useTable } from 'react-table';


const UsersDetails = ({users}) => {

    const COLUMNS = [
        {
            Header: "Check",
            accessor: "check", // Add a virtual accessor for the check column
            Cell: ({ row }) => (
              <input type="checkbox" checked={row.original.check} readOnly 
              />
            ),
        },
        {
           Header: "Name",
           accessor: "fullname"
        },
        {
           Header: "Position",
           accessor: "position"
        },
        {
           Header: "e-Mail",
           accessor: "email"
        },
        {
           Header: "Last login",
           accessor: "lastlogin"
        },
        {
           Header: "Status",
           accessor: "status"
        }
    ];

    const columns = useMemo(()=> COLUMNS, []);
    const data = useMemo(()=> users, []);
    /////////////////////////////////////////////////////////

    const IndeterminateCheckbox = React.forwardRef(
        ({ indeterminate, ...rest }, ref) => {
          const defaultRef = React.useRef();
          const resolvedRef = ref || defaultRef;
      
          React.useEffect(() => {
            resolvedRef.current.indeterminate = indeterminate;
          }, [resolvedRef, indeterminate]);
      
          return (
            <>
              <input type="checkbox" ref={resolvedRef} {...rest} />
            </>
          );
        }
      );

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        prepareRow,
        page,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        selectedFlatRows,
        state: { pageIndex, selectedRowIds }
      } = useTable(
        {
          columns,
          data
        },
        usePagination,
        useRowSelect,
        (hooks) => {
          hooks.visibleColumns.push((columns) => [
            {
              id: "anyThing",
              Header: ({ toggleRowSelected, isAllPageRowsSelected, page }) => {
                const modifiedOnChange = (event) => {
                  page.forEach((row) => {
                    //check each row if it is not disabled
                    !row.original.disabled &&
                      toggleRowSelected(row.id, event.currentTarget.checked);
                  });
                };
    
                //Count number of selectable and selected rows in the current page
                //to determine the state of select all checkbox
                let selectableRowsInCurrentPage = 0;
                let selectedRowsInCurrentPage = 0;
                page.forEach((row) => {
                  row.isSelected && selectedRowsInCurrentPage++;
                  !row.original.disabled && selectableRowsInCurrentPage++;
                });
    
                //If there are no selectable rows in the current page
                //select all checkbox will be disabled -> see page 2
                const disabled = selectableRowsInCurrentPage === 0;
                const checked =
                  (isAllPageRowsSelected ||
                    selectableRowsInCurrentPage === selectedRowsInCurrentPage) &&
                  !disabled;
    
                return (
                  <div>
                    <IndeterminateCheckbox
                      onChange={modifiedOnChange}
                      checked={checked}
                      disabled={disabled}
                    />
                  </div>
                );
              },
              Cell: ({ row }) => (
                <div>
                  <IndeterminateCheckbox
                    {...row.getToggleRowSelectedProps()}
                    disabled={row.original.disabled}
                  />
                </div>
              )
            },
            ...columns
          ]);
        }
      );
    
      // Render the UI for your table
      return (
        <>
          <table {...getTableProps()}>
            <thead>
              {headerGroups.map((headerGroup) => (
                <tr {...headerGroup.getHeaderGroupProps()}>
                  {headerGroup.headers.map((column) => (
                    <th {...column.getHeaderProps()}>{column.render("Header")}</th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody {...getTableBodyProps()}>
              {page.map((row, i) => {
                prepareRow(row);
                return (
                  <tr {...row.getRowProps()}>
                    {row.cells.map((cell) => {
                      return (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      );
                    })}
                  </tr>
                );
              })}
            </tbody>
          </table>
          <div className="pagination">
            <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
              {"<<"}
            </button>{" "}
            <button onClick={() => previousPage()} disabled={!canPreviousPage}>
              {"<"}
            </button>{" "}
            <button onClick={() => nextPage()} disabled={!canNextPage}>
              {">"}
            </button>{" "}
            <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
              {">>"}
            </button>{" "}
            <span>
              Page{" "}
              <strong>
                {pageIndex + 1} of {pageOptions.length}
              </strong>{" "}
            </span>
            <pre>
              <code>
                {JSON.stringify(
                  {
                    selectedRowIds: selectedRowIds,
                    "selectedFlatRows[].original": selectedFlatRows.map(
                      (d) => d.original
                    )
                  },
                  null,
                  2
                )}
              </code>
            </pre>
          </div>
        </>
      );
}
 
export default UsersDetails;
