import React, { useEffect, useState } from "react";
import { useAuthContext } from '../hooks/useAuthContext';
import UsersDetails from '../components/UsersDetails';
import { Navigate } from 'react-router-dom';

const Main = () => {
    const { user, logout } = useAuthContext();
    console.log(user);
    const [users, setUsers] = useState(null);
    const [tokenValid, setTokenValid] = useState(true);
    console.log("tokenValid: " + tokenValid);

    useEffect(() => {
        const fetchData = async () => {
            const loginStatus = await fetch('/api/user'+user._id, {
                method: 'GET'
            });
            console.log('loginstatus: ' + loginStatus.body.value);
            if(loginStatus === undefined){
                localStorage.removeItem('user');
            }
            try {
                const response = await fetch('/api/user', {
                    method: 'GET',
                    headers: { 'Authorization': `Bearer ${user.token}` }
                });

                if (!response.ok) {
                    // Check if the response status is 401 (Unauthorized)
                    if (response.status === 401) {
                        // Token is invalid, log the user out
                        logout();
                        setTokenValid(false);
                    } else {
                        // Handle other errors, maybe show an error message
                        console.error('Failed to fetch user data');
                    }
                    return;
                }

                const data = await response.json();
                setUsers(data);
            } catch (error) {
                console.error('Error fetching user data', error);
            }
        };

        if (user && tokenValid) {
            fetchData();
        }
    }, []);

    const COLUMNS = [
        {
           Header: "Name",
           accessor: "fullname"
        },
        {
           Header: "Position",
           accessor: "position"
        },
        {
           Header: "e-Mail",
           accessor: "email"
        },
        {
           Header: "Last login",
           accessor: "lastlogin"
        },
        {
           Header: "Status",
           accessor: "status"
        }
    ];

    // Redirect to login page if token is not valid
    if (!tokenValid) {
        return <Navigate to='/login' />;
    }

    return (
        <div className="main">
            {users && <UsersDetails users={users}/>}
        </div>
    );
}

export default Main;
